import React from 'react';
import './About.css';
import BackgroundVideo from '../../sources/BgVideo.mp4'; // Import the locally stored video

const About = () => {
  return (
    <div className='about'>
      <video autoPlay loop muted className='bg-video'>
        <source src={BackgroundVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className='content'>
        <h1>
        “Hey Sukrit,<br />Welcome aboard the frontend express! Get ready to paint the digital canvas with your code.”
        </h1>
      </div>
    </div>
  );
}

export default About;
